    private fun setLightStatusBar() {
        //setting light mode status bar
        requireActivity().window.decorView.systemUiVisibility = View.SYSTEM_UI_FLAG_LIGHT_STATUS_BAR
        val view = View(requireContext())
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {
            var flags: Int = view.systemUiVisibility
            flags = flags or View.SYSTEM_UI_FLAG_LIGHT_STATUS_BAR
            view.systemUiVisibility = flags
            requireActivity().window.statusBarColor = Color.WHITE
        }
    }

    private fun permissionRequest() {
        if (checkCallingOrSelfPermission(
                requireContext(),
                Manifest.permission.ACCESS_FINE_LOCATION
            ) != PermissionChecker.PERMISSION_GRANTED
        ) {
            request()
        } else {
            setUpCamera()
        }
    }

    private fun setUpCamera() {
        val locationHelper = LocationHelper()
        locationHelper.startLocationUpdates(requireContext())
        locationHelper.addLocationlistener(object : LocationHelper.LocationListener {
            override fun onLocationChanged(location: Location) {
                if (isFirstTime) {
                    val target = LatLng(location.latitude, location.longitude)
                    val cameraUpdate = CameraPosition.Builder().target(target).zoom(16f).build()
                    mMap.moveCamera(
                        CameraUpdateFactory.newCameraPosition(cameraUpdate)
                    )
                    isFirstTime = false
                }


//                if (userLocationMarker == null) {
////                    val bitmap = BitmapFactory.decodeResource(resources,R.drawable.ic_location_svgrepo_com)
////                    val icon = BitmapDescriptorFactory.fromBitmap(bitmap)
//                    userLocationMarker = mMap.addMarker(
//                        MarkerOptions()
//                            .position(target)
//                    )
//                } else {
//                    userLocationMarker?.position = target
//                }


            }
        })
    }

    private fun request() {
        Dexter.withContext(requireContext())
            .withPermission(Manifest.permission.ACCESS_FINE_LOCATION)
            .withListener(object : PermissionListener {
                override fun onPermissionGranted(response: PermissionGrantedResponse?) { /* ... */
                    setUpCamera()
                }

                override fun onPermissionDenied(response: PermissionDeniedResponse?) { /* ... */
                    //open settings

                }

                override fun onPermissionRationaleShouldBeShown(
                    p0: com.karumi.dexter.listener.PermissionRequest?,
                    p1: PermissionToken?
                ) {
                    p1?.continuePermissionRequest()
                }

            }).check()
    }

    private fun findMultipleLocation() {
        locationList.add(location1)
        locationList.add(location2)
        for (i in locationList.indices) {
            mMap.addMarker(MarkerOptions().position(locationList[i]).title("Marker"))
            mMap.animateCamera(CameraUpdateFactory.zoomTo(18.0f))
            mMap.moveCamera(CameraUpdateFactory.newLatLng(locationList[i]))
        }


    }


    private fun calculateDestination(response: GeoResponse, latlng: LatLng): GeoCodeInfo? {
        var geoCodeInfo: GeoCodeInfo? = null
        var minDistance = Double.MAX_VALUE

        for (datum in response.data) {
            if (minDistance > distance(
                    datum.latitude,
                    datum.longitude,
                    latlng.latitude,
                    latlng.longitude
                )
            ) {
                minDistance =
                    distance(datum.latitude, datum.longitude, latlng.latitude, latlng.longitude)
                geoCodeInfo = datum
            }
        }

        return geoCodeInfo

    }

    private fun btnMyLocationClickManager() {
